import React, { useEffect, useRef, useState } from 'react';

const LoadingVideo = ({ mobileSrc, desktopSrc }) => {
  const videoRef = useRef(null);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [videoLoaded, setVideoLoaded] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const video = videoRef.current;
    if (video) {
      // Force load the video
      video.load();
      
      // Play the video when it's loaded
      const handleCanPlay = () => {
        setVideoLoaded(true);
        video.play().catch(error => {
          console.error('Error playing video:', error);
        });
      };
      
      video.addEventListener('canplay', handleCanPlay);
      
      return () => {
        video.removeEventListener('canplay', handleCanPlay);
      };
    }
  }, [isMobile]);

  return (
    <div className="relative w-64 h-64">
      {!videoLoaded && (
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500"></div>
        </div>
      )}
      <video 
        ref={videoRef}
        autoPlay 
        loop 
        muted 
        playsInline
        className={`w-full h-full ${videoLoaded ? 'opacity-100' : 'opacity-0'} transition-opacity duration-300`}
      >
        <source src={isMobile ? mobileSrc : desktopSrc} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
    </div>
  );
};

export default LoadingVideo;
