import React, { useState } from 'react';
import { aiAPI } from '../utils/api';

const ContractReviewer = () => {
  const [contractCode, setContractCode] = useState('');
  const [review, setReview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  
  // Handle contract code change
  const handleCodeChange = (e) => {
    setContractCode(e.target.value);
  };
  
  // Handle review submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!contractCode.trim()) {
      setError('Please enter contract code to review');
      return;
    }
    
    setLoading(true);
    setError('');
    setReview(null);
    
    try {
      const result = await aiAPI.reviewContract(contractCode);
      
      if (result.success) {
        setReview(result.data.review);
      } else {
        setError(result.message || 'Failed to review contract. Please try again.');
      }
    } catch (error) {
      console.error('Error reviewing contract:', error);
      setError('An unexpected error occurred. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  
  // Sample contract code
  const sampleContractCode = `// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract SimpleStorage {
    uint256 private storedData;
    
    function set(uint256 x) public {
        storedData = x;
    }
    
    function get() public view returns (uint256) {
        return storedData;
    }
}`;
  
  // Load sample code
  const loadSampleCode = () => {
    setContractCode(sampleContractCode);
  };
  
  return (
    <div className="bg-gray-50 min-h-screen">
      <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8 py-10">
        <div className="mb-8">
          <h1 className="text-2xl font-bold text-gray-900">Smart Contract Reviewer</h1>
          <p className="mt-1 text-sm text-gray-500">
            Analyze smart contracts for security vulnerabilities, best practices, and optimization opportunities.
          </p>
        </div>
        
        <div className="grid grid-cols-1 gap-8 lg:grid-cols-2">
          {/* Contract Code Input */}
          <div className="bg-white shadow rounded-lg p-6">
            <form onSubmit={handleSubmit}>
              <div className="mb-4">
                <div className="flex justify-between items-center">
                  <label htmlFor="contractCode" className="block text-sm font-medium text-gray-700 mb-2">
                    Contract Code
                  </label>
                  <button
                    type="button"
                    onClick={loadSampleCode}
                    className="text-sm text-indigo-600 hover:text-indigo-500"
                  >
                    Load Sample
                  </button>
                </div>
                <textarea
                  id="contractCode"
                  name="contractCode"
                  rows={20}
                  value={contractCode}
                  onChange={handleCodeChange}
                  placeholder="Paste your Solidity smart contract code here..."
                  className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6 font-mono"
                  required
                />
              </div>
              
              {error && (
                <div className="mb-4 rounded-md bg-red-50 p-4">
                  <div className="flex">
                    <div className="ml-3">
                      <h3 className="text-sm font-medium text-red-800">{error}</h3>
                    </div>
                  </div>
                </div>
              )}
              
              <div className="flex justify-end">
                <button
                  type="submit"
                  disabled={loading || !contractCode.trim()}
                  className="rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600 disabled:bg-indigo-400"
                >
                  {loading ? 'Analyzing...' : 'Review Contract'}
                </button>
              </div>
            </form>
          </div>
          
          {/* Review Results */}
          <div className="bg-white shadow rounded-lg p-6">
            <h2 className="text-lg font-medium text-gray-900 mb-4">Review Results</h2>
            
            {loading ? (
              <div className="flex justify-center items-center h-64">
                <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500"></div>
              </div>
            ) : review ? (
              <div className="space-y-6">
                {review.summary && (
                  <div>
                    <h3 className="text-sm font-medium text-gray-900 mb-2">Summary</h3>
                    <p className="text-sm text-gray-600">{review.summary}</p>
                  </div>
                )}
                
                {review.vulnerabilities && review.vulnerabilities.length > 0 && (
                  <div>
                    <h3 className="text-sm font-medium text-gray-900 mb-2">Security Vulnerabilities</h3>
                    <ul className="space-y-2">
                      {review.vulnerabilities.map((vulnerability, index) => (
                        <li key={index} className="rounded-md bg-red-50 p-3">
                          <div className="flex">
                            <div className="flex-shrink-0">
                              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="h-5 w-5 text-red-400">
                                <path strokeLinecap="round" strokeLinejoin="round" d="M12 9v3.75m-9.303 3.376c-.866 1.5.217 3.374 1.948 3.374h14.71c1.73 0 2.813-1.874 1.948-3.374L13.949 3.378c-.866-1.5-3.032-1.5-3.898 0L2.697 16.126ZM12 15.75h.007v.008H12v-.008Z" />
                              </svg>
                            </div>
                            <div className="ml-3">
                              <h4 className="text-sm font-medium text-red-800">{vulnerability.title}</h4>
                              <div className="mt-1 text-sm text-red-700">
                                <p>{vulnerability.description}</p>
                                {vulnerability.location && (
                                  <p className="mt-1 font-mono text-xs">Location: {vulnerability.location}</p>
                                )}
                              </div>
                              {vulnerability.recommendation && (
                                <div className="mt-2">
                                  <h5 className="text-xs font-medium text-red-800">Recommendation:</h5>
                                  <p className="text-sm text-red-700">{vulnerability.recommendation}</p>
                                </div>
                              )}
                            </div>
                          </div>
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
                
                {review.bestPractices && review.bestPractices.length > 0 && (
                  <div>
                    <h3 className="text-sm font-medium text-gray-900 mb-2">Best Practices</h3>
                    <ul className="space-y-2">
                      {review.bestPractices.map((practice, index) => (
                        <li key={index} className="rounded-md bg-yellow-50 p-3">
                          <div className="flex">
                            <div className="flex-shrink-0">
                              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="h-5 w-5 text-yellow-400">
                                <path strokeLinecap="round" strokeLinejoin="round" d="M12 9v3.75m9-.75a9 9 0 1 1-18 0 9 9 0 0 1 18 0Zm-9 3.75h.008v.008H12v-.008Z" />
                              </svg>
                            </div>
                            <div className="ml-3">
                              <h4 className="text-sm font-medium text-yellow-800">{practice.title}</h4>
                              <div className="mt-1 text-sm text-yellow-700">
                                <p>{practice.description}</p>
                              </div>
                              {practice.recommendation && (
                                <div className="mt-2">
                                  <h5 className="text-xs font-medium text-yellow-800">Recommendation:</h5>
                                  <p className="text-sm text-yellow-700">{practice.recommendation}</p>
                                </div>
                              )}
                            </div>
                          </div>
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
                
                {review.optimizations && review.optimizations.length > 0 && (
                  <div>
                    <h3 className="text-sm font-medium text-gray-900 mb-2">Optimization Opportunities</h3>
                    <ul className="space-y-2">
                      {review.optimizations.map((optimization, index) => (
                        <li key={index} className="rounded-md bg-green-50 p-3">
                          <div className="flex">
                            <div className="flex-shrink-0">
                              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="h-5 w-5 text-green-400">
                                <path strokeLinecap="round" strokeLinejoin="round" d="M3 13.125C3 12.504 3.504 12 4.125 12h2.25c.621 0 1.125.504 1.125 1.125v6.75C7.5 20.496 6.996 21 6.375 21h-2.25A1.125 1.125 0 0 1 3 19.875v-6.75ZM9.75 8.625c0-.621.504-1.125 1.125-1.125h2.25c.621 0 1.125.504 1.125 1.125v11.25c0 .621-.504 1.125-1.125 1.125h-2.25a1.125 1.125 0 0 1-1.125-1.125V8.625ZM16.5 4.125c0-.621.504-1.125 1.125-1.125h2.25C20.496 3 21 3.504 21 4.125v15.75c0 .621-.504 1.125-1.125 1.125h-2.25a1.125 1.125 0 0 1-1.125-1.125V4.125Z" />
                              </svg>
                            </div>
                            <div className="ml-3">
                              <h4 className="text-sm font-medium text-green-800">{optimization.title}</h4>
                              <div className="mt-1 text-sm text-green-700">
                                <p>{optimization.description}</p>
                              </div>
                              {optimization.recommendation && (
                                <div className="mt-2">
                                  <h5 className="text-xs font-medium text-green-800">Recommendation:</h5>
                                  <p className="text-sm text-green-700">{optimization.recommendation}</p>
                                </div>
                              )}
                            </div>
                          </div>
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            ) : (
              <div className="flex flex-col items-center justify-center h-64 text-center">
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-12 h-12 text-gray-400 mb-4">
                  <path strokeLinecap="round" strokeLinejoin="round" d="m11.25 11.25.041-.02a.75.75 0 0 1 1.063.852l-.708 2.836a.75.75 0 0 0 1.063.853l.041-.021M21 12a9 9 0 1 1-18 0 9 9 0 0 1 18 0Zm-9-3.75h.008v.008H12V8.25Z" />
                </svg>
                <p className="text-gray-500">Enter your smart contract code and click "Review Contract" to get an analysis.</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ContractReviewer;
