import React from 'react';
import VideoPlayer from './VideoPlayer';

const Loading = ({ message = 'Loading...', showVideo = true }) => {
  return (
    <div className="flex flex-col justify-center items-center h-screen bg-gray-50">
      {showVideo ? (
        <div className="w-full h-full absolute inset-0">
          <VideoPlayer 
            src={window.innerWidth < 768 ? "/assets/Load2.mp4" : "/assets/Load.mp4"}
            className="w-full h-full object-cover"
          />
          <div className="absolute inset-0 flex flex-col items-center justify-center">
            <h1 className="text-3xl font-bold text-indigo-600 mt-4 bg-white/70 px-6 py-2 rounded-lg">DigiLex</h1>
            <p className="text-gray-700 mt-2 bg-white/70 px-4 py-1 rounded">{message}</p>
          </div>
        </div>
      ) : (
        <div className="flex flex-col items-center justify-center">
          <div className="w-16 h-16 mb-4">
            <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-indigo-500"></div>
          </div>
          <h1 className="text-3xl font-bold text-indigo-600 mt-4">DigiLex</h1>
          <p className="text-gray-500 mt-2">{message}</p>
        </div>
      )}
    </div>
  );
};

export default Loading;
