import React, { useState, useEffect } from 'react';

const BackgroundImage = ({ mobileSrc, desktopSrc, opacity = 1, position = 'center', size = 'cover' }) => {
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [imageLoaded, setImageLoaded] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const img = new Image();
    img.src = isMobile ? mobileSrc : desktopSrc;
    img.onload = () => setImageLoaded(true);
  }, [isMobile, mobileSrc, desktopSrc]);

  return (
    <div 
      className={`absolute inset-0 transition-opacity duration-500 ${imageLoaded ? 'opacity-100' : 'opacity-0'}`} 
      style={{ 
        backgroundImage: `url('${isMobile ? mobileSrc : desktopSrc}')`,
        backgroundSize: size,
        backgroundPosition: position,
        backgroundRepeat: 'no-repeat',
        opacity: opacity
      }}
    />
  );
};

export default BackgroundImage;
