import React, { Suspense } from 'react';
import { Routes, Route } from 'react-router-dom';
import { AuthProvider } from './contexts/AuthContext';
import { Web3Provider } from './contexts/Web3Context';

// Components
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Loading from './components/Loading';

// Lazy load pages for better performance
const Dashboard = React.lazy(() => import('./pages/Dashboard'));
const DocumentGenerator = React.lazy(() => import('./pages/DocumentGenerator'));
const ContractReviewer = React.lazy(() => import('./pages/ContractReviewer'));
const DAOCreator = React.lazy(() => import('./pages/DAOCreator'));
const LegalChatbot = React.lazy(() => import('./pages/LegalChatbot'));
const Home = React.lazy(() => import('./pages/Home'));

function App() {
  return (
    <AuthProvider>
      <Web3Provider>
        <div className="flex flex-col min-h-screen">
          <Navbar />
          <main className="flex-grow">
            <Suspense fallback={<Loading showVideo={false} message="Loading page..." />}>
              <Routes>
                {/* Making dashboard publicly accessible for now */}
                <Route path="/dashboard" element={<Dashboard />} />
                <Route path="/document-generator" element={<DocumentGenerator />} />
                <Route path="/contract-reviewer" element={<ContractReviewer />} />
                <Route path="/dao-creator" element={<DAOCreator />} />
                <Route path="/legal-chatbot" element={<LegalChatbot />} />
                <Route path="/" element={<Home />} />
              </Routes>
            </Suspense>
          </main>
          <Footer />
        </div>
      </Web3Provider>
    </AuthProvider>
  );
}

export default App;
