import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useWeb3 } from '../contexts/Web3Context';
import { aiAPI, documentAPI } from '../utils/api';
import html2pdf from 'html2pdf.js';

const documentTypes = [
  { id: 'nda', name: 'Non-Disclosure Agreement (NDA)', description: 'A confidentiality agreement under Indian contract law' },
  { id: 'employment', name: 'Employment Contract', description: 'Employment agreement compliant with Indian labor laws' },
  { id: 'lease', name: 'Rental/Lease Agreement', description: 'Property rental agreement following Indian tenancy laws' },
  { id: 'terms', name: 'Terms of Service', description: 'Website/app terms of service under Indian IT Act' },
  { id: 'privacy', name: 'Privacy Policy', description: 'Privacy policy compliant with Indian data protection regulations' },
  { id: 'partnership', name: 'Partnership Deed', description: 'Partnership agreement under Indian Partnership Act, 1932' },
  { id: 'company', name: 'Memorandum of Association', description: 'For company incorporation under Companies Act, 2013' },
  { id: 'service', name: 'Service Agreement', description: 'General service contract under Indian contract law' },
  { id: 'will', name: 'Last Will & Testament', description: 'Will document according to Indian succession laws' },
  { id: 'consultancy', name: 'Consultancy Agreement', description: 'Contract for consulting services under Indian law' }
];

const DocumentGenerator = () => {
  const navigate = useNavigate();
  const { connected, account, connectWallet } = useWeb3();
  const documentRef = useRef(null);
  
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  
  const [formData, setFormData] = useState({
    title: '',
    documentType: '',
    parameters: {},
    privateKey: '',
    saveToBlockchain: false
  });
  
  const [generatedContent, setGeneratedContent] = useState('');
  
  // Handle document type selection
  const handleDocumentTypeSelect = (documentType) => {
    setFormData({
      ...formData,
      documentType,
      parameters: getDefaultParameters(documentType)
    });
    setStep(2);
  };
  
  // Get default parameters based on document type
  const getDefaultParameters = (documentType) => {
    switch (documentType) {
      case 'nda':
        return {
          partyOne: '',
          partyTwo: '',
          effectiveDate: '',
          duration: '1 year',
          state: '',
          confidentialInfo: '',
          purpose: ''
        };
      case 'employment':
        return {
          employerName: '',
          employeeName: '',
          position: '',
          startDate: '',
          salary: '',
          benefits: '',
          terminationTerms: ''
        };
      case 'lease':
        return {
          landlordName: '',
          tenantName: '',
          propertyAddress: '',
          leaseStart: '',
          leaseDuration: '',
          monthlyRent: '',
          securityDeposit: ''
        };
      default:
        return {};
    }
  };
  
  // Handle form input changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    
    if (name.includes('.')) {
      // Handle nested parameters
      const [parent, child] = name.split('.');
      setFormData({
        ...formData,
        [parent]: {
          ...formData[parent],
          [child]: type === 'checkbox' ? checked : value
        }
      });
    } else {
      // Handle top-level fields
      setFormData({
        ...formData,
        [name]: type === 'checkbox' ? checked : value
      });
    }
  };
  
  // Handle connect wallet
  const handleConnectWallet = async () => {
    try {
      await connectWallet();
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };
  
  // Handle preview document
  const handlePreview = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    
    try {
      console.log('Generating document:', { documentType: formData.documentType, parameters: formData.parameters });
      const response = await aiAPI.generateDocument(formData.documentType, formData.parameters);
      
      console.log('Document generation response:', response);
      
      if (response.data.success) {
        const content = response.data.data.content;
        if (!content) {
          throw new Error('No content received in the response');
        }
        setGeneratedContent(content);
        setStep(3);
      } else {
        setError(response.data.message || 'Failed to generate document. Please try again.');
      }
    } catch (error) {
      console.error('Error generating document:', error);
      setError(error.response?.data?.message || error.message || 'An unexpected error occurred. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  
  // Handle save document
  const handleSave = async () => {
    setLoading(true);
    setError('');
    
    try {
      const documentData = {
        title: formData.title,
        documentType: formData.documentType,
        content: generatedContent,
        parameters: formData.parameters,
        privateKey: formData.saveToBlockchain ? formData.privateKey : undefined
      };
      
      const result = await documentAPI.createDocument(documentData);
      
      if (result.success) {
        setSuccess(true);
        setTimeout(() => {
          navigate(`/documents/${result.data._id}`);
        }, 2000);
      } else {
        setError(result.message || 'Failed to save document. Please try again.');
      }
    } catch (error) {
      console.error('Error saving document:', error);
      setError('An unexpected error occurred. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  
  // Handle download as PDF
  const handleDownloadPDF = async () => {
    if (!generatedContent || !documentRef.current) return;
    
    const element = documentRef.current;
    const opt = {
      margin: [20, 20],
      filename: `${formData.title || 'legal-document'}.pdf`,
      image: { type: 'jpeg', quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: 'mm', format: 'a4', orientation: 'portrait' }
    };
    
    try {
      await html2pdf().set(opt).from(element).save();
    } catch (error) {
      console.error('Error generating PDF:', error);
      setError('Failed to generate PDF. Please try again.');
    }
  };
  
  // Render document type selection
  const renderDocumentTypeSelection = () => {
    return (
      <div>
        <h2 className="text-lg font-medium text-gray-900 dark:text-gray-100 mb-4">Select Indian Legal Document Type</h2>
        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
          {documentTypes.map((type) => (
            <button
              key={type.id}
              type="button"
              onClick={() => handleDocumentTypeSelect(type.id)}
              className="relative flex flex-col rounded-lg border border-gray-300 dark:border-gray-700 bg-white dark:bg-gray-800 p-6 text-left hover:border-gray-400 dark:hover:border-gray-600 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
            >
              <h3 className="text-sm font-medium text-gray-900 dark:text-white">{type.name}</h3>
              <p className="mt-2 text-xs text-gray-500 dark:text-gray-400">{type.description}</p>
            </button>
          ))}
        </div>
        <div className="mt-6 bg-indigo-50 dark:bg-indigo-900/30 rounded-lg p-4">
          <p className="text-xs text-indigo-700 dark:text-indigo-300">
            All documents are generated according to Indian legal standards and include relevant citations to Indian laws where appropriate.
            These documents are provided as templates and should be reviewed by a qualified legal professional before use.
          </p>
        </div>
      </div>
    );
  };
  
  // Render parameter form based on document type
  const renderParameterForm = () => {
    return (
      <form onSubmit={handlePreview}>
        <div className="space-y-6">
          <div>
            <h2 className="text-lg font-medium text-gray-900 mb-4">Document Details</h2>
            <div className="grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6">
              <div className="sm:col-span-4">
                <label htmlFor="title" className="block text-sm font-medium leading-6 text-gray-900">
                  Document Title
                </label>
                <div className="mt-2">
                  <input
                    type="text"
                    name="title"
                    id="title"
                    value={formData.title}
                    onChange={handleChange}
                    required
                    className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                  />
                </div>
              </div>
            </div>
          </div>
          
          <div>
            <h2 className="text-lg font-medium text-gray-900 mb-4">Document Parameters</h2>
            <div className="grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6">
              {Object.entries(formData.parameters).map(([key, value]) => (
                <div key={key} className="sm:col-span-3">
                  <label htmlFor={`parameters.${key}`} className="block text-sm font-medium leading-6 text-gray-900">
                    {key.replace(/([A-Z])/g, ' $1').replace(/^./, (str) => str.toUpperCase())}
                  </label>
                  <div className="mt-2">
                    <input
                      type="text"
                      name={`parameters.${key}`}
                      id={`parameters.${key}`}
                      value={value}
                      onChange={handleChange}
                      required
                      className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                    />
                  </div>
                </div>
              ))}
            </div>
          </div>
          
          <div className="flex items-center justify-end gap-x-6">
            <button
              type="button"
              onClick={() => setStep(1)}
              className="text-sm font-semibold leading-6 text-gray-900"
            >
              Back
            </button>
            <button
              type="submit"
              disabled={loading}
              className="rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600 disabled:bg-indigo-400"
            >
              {loading ? 'Generating...' : 'Preview Document'}
            </button>
          </div>
        </div>
      </form>
    );
  };
  
  // Render document preview
  const renderDocumentPreview = () => {
    if (!generatedContent) {
      return (
        <div className="text-center py-12">
          <p className="text-gray-500">No document content available. Please go back and try generating again.</p>
        </div>
      );
    }

    return (
      <div>
        <h2 className="text-lg font-medium text-gray-900 mb-4">Document Preview</h2>
        
        <div className="mt-4 p-6 border border-gray-300 rounded-md bg-white">
          <div ref={documentRef} className="prose prose-sm max-w-none">
            <pre className="whitespace-pre-wrap font-serif text-base leading-relaxed">{generatedContent}</pre>
          </div>
        </div>
        
        <div className="mt-6">
          <h3 className="text-sm font-medium text-gray-900 mb-2">Document Options</h3>
          
          <div className="flex items-start mb-4 space-x-4">
            <button
              type="button"
              onClick={handleDownloadPDF}
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
              Download as PDF
            </button>
            
            <div className="flex items-center">
              <input
                id="saveToBlockchain"
                name="saveToBlockchain"
                type="checkbox"
                checked={formData.saveToBlockchain}
                onChange={handleChange}
                className="h-4 w-4 rounded border-gray-300 text-indigo-600 focus:ring-indigo-600"
              />
              <div className="ml-3 text-sm leading-6">
                <label htmlFor="saveToBlockchain" className="font-medium text-gray-900">
                  Save to Blockchain
                </label>
                <p className="text-gray-500">
                  Store document hash on the blockchain for immutable proof of existence.
                </p>
              </div>
            </div>
          </div>
          
          {formData.saveToBlockchain && (
            <div className="mb-4">
              {connected ? (
                <div className="sm:col-span-4">
                  <label htmlFor="privateKey" className="block text-sm font-medium leading-6 text-gray-900">
                    Private Key (for signing transaction)
                  </label>
                  <div className="mt-2">
                    <input
                      type="password"
                      name="privateKey"
                      id="privateKey"
                      value={formData.privateKey}
                      onChange={handleChange}
                      required={formData.saveToBlockchain}
                      className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                    />
                    <p className="mt-1 text-sm text-gray-500">
                      Your private key is only used for signing the transaction and is not stored.
                    </p>
                  </div>
                </div>
              ) : (
                <div className="rounded-md bg-yellow-50 p-4">
                  <div className="flex">
                    <div className="ml-3">
                      <h3 className="text-sm font-medium text-yellow-800">Wallet not connected</h3>
                      <div className="mt-2 text-sm text-yellow-700">
                        <p>
                          You need to connect your wallet to save documents to the blockchain.
                        </p>
                      </div>
                      <div className="mt-4">
                        <button
                          type="button"
                          onClick={handleConnectWallet}
                          className="rounded-md bg-yellow-50 px-2 py-1.5 text-sm font-medium text-yellow-800 hover:bg-yellow-100 focus:outline-none focus:ring-2 focus:ring-yellow-600 focus:ring-offset-2 focus:ring-offset-yellow-50"
                        >
                          Connect Wallet
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
        
        {error && (
          <div className="mt-4 rounded-md bg-red-50 p-4">
            <div className="flex">
              <div className="ml-3">
                <h3 className="text-sm font-medium text-red-800">{error}</h3>
              </div>
            </div>
          </div>
        )}
        
        {success && (
          <div className="mt-4 rounded-md bg-green-50 p-4">
            <div className="flex">
              <div className="ml-3">
                <h3 className="text-sm font-medium text-green-800">Document saved successfully!</h3>
                <div className="mt-2 text-sm text-green-700">
                  <p>Redirecting to document view...</p>
                </div>
              </div>
            </div>
          </div>
        )}
        
        <div className="mt-6 flex items-center justify-end gap-x-6">
          <button
            type="button"
            onClick={() => setStep(2)}
            className="text-sm font-semibold leading-6 text-gray-900"
          >
            Back
          </button>
          <button
            type="button"
            onClick={handleSave}
            disabled={loading || success}
            className="rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600 disabled:bg-indigo-400"
          >
            {loading ? 'Saving...' : 'Save Document'}
          </button>
        </div>
      </div>
    );
  };
  
  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-5xl mx-auto">
        <h1 className="text-2xl font-bold text-gray-900 dark:text-gray-100 mb-6">Legal Document Generator</h1>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="md:col-span-1">
            <div className="card p-6 mb-6">
              <h2 className="text-lg font-medium text-gray-900 dark:text-gray-100 mb-4">Document Type</h2>
              
              <div className="space-y-4">
                {documentTypes.map((type) => (
                  <div key={type.id} className="flex items-center">
                    <input
                      id={type.id}
                      name="documentType"
                      type="radio"
                      value={type.id}
                      checked={formData.documentType === type.id}
                      onChange={() => handleDocumentTypeSelect(type.id)}
                      className="h-4 w-4 text-indigo-600 dark:text-indigo-500 focus:ring-indigo-500 dark:focus:ring-indigo-400 border-gray-300 dark:border-gray-600"
                    />
                    <label htmlFor={type.id} className="ml-3 block text-sm font-medium text-gray-700 dark:text-gray-300">
                      {type.name}
                    </label>
                  </div>
                ))}
              </div>
            </div>
            
            {formData.documentType && (
              <div className="card p-6">
                <h2 className="text-lg font-medium text-gray-900 dark:text-gray-100 mb-4">Parameters</h2>
                
                <form onSubmit={handlePreview} className="space-y-4">
                  {getParameterFields()}
                  
                  {error && (
                    <div className="p-3 bg-red-100 dark:bg-red-900/30 text-red-700 dark:text-red-300 rounded-md shadow-sm">
                      {error}
                    </div>
                  )}
                  
                  <div className="flex justify-end">
                    <button
                      type="submit"
                      disabled={loading}
                      className="btn-primary flex items-center"
                    >
                      {loading ? (
                        <>
                          <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                          </svg>
                          Generating...
                        </>
                      ) : (
                        'Generate Document'
                      )}
                    </button>
                  </div>
                </form>
              </div>
            )}
          </div>
          
          <div className="md:col-span-2">
            <div className="card p-6 h-full">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-lg font-medium text-gray-900 dark:text-gray-100">Document Preview</h2>
                
                {generatedContent && (
                  <div className="flex space-x-2">
                    <button
                      onClick={handleSave}
                      className="btn-secondary flex items-center text-sm"
                    >
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7H5a2 2 0 00-2 2v9a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2h-3m-1 4l-3 3m0 0l-3-3m3 3V4" />
                      </svg>
                      Save
                    </button>
                    
                    <button
                      onClick={handleDownloadPDF}
                      className="btn-secondary flex items-center text-sm"
                    >
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 10v6m0 0l-3-3m3 3l3-3m2 8H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                      </svg>
                      PDF
                    </button>
                  </div>
                )}
              </div>
              
              {loading ? (
                <div className="flex justify-center items-center h-96">
                  <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-indigo-500 dark:border-indigo-400"></div>
                </div>
              ) : generatedContent ? (
                <div 
                  id="document-content" 
                  className="prose dark:prose-invert max-w-none p-6 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-md shadow-sm overflow-auto h-[600px]"
                  dangerouslySetInnerHTML={{ __html: generatedContent }}
                ></div>
              ) : (
                <div className="flex flex-col items-center justify-center h-96 text-center">
                  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-16 h-16 text-gray-300 dark:text-gray-600 mb-4">
                    <path strokeLinecap="round" strokeLinejoin="round" d="M19.5 14.25v-2.625a3.375 3.375 0 00-3.375-3.375h-1.5A1.125 1.125 0 0113.5 7.125v-1.5a3.375 3.375 0 00-3.375-3.375H8.25m0 12.75h7.5m-7.5 3H12M10.5 2.25H5.625c-.621 0-1.125.504-1.125 1.125v17.25c0 .621.504 1.125 1.125 1.125h12.75c.621 0 1.125-.504 1.125-1.125V11.25a9 9 0 00-9-9z" />
                  </svg>
                  <p className="text-gray-500 dark:text-gray-400">Select a document type and fill in the parameters to generate a document.</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DocumentGenerator;
