import axios from 'axios';

// Use environment variable or fallback to hardcoded URL if not available
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

console.log('API URL being used:', API_URL); // Debug log

// Create axios instance with base URL
const api = axios.create({
  baseURL: API_URL,
  withCredentials: true,
  headers: {
    'Content-Type': 'application/json'
  },
  timeout: 60000 // Set a global timeout of 60 seconds
});

// Add request interceptor for logging
api.interceptors.request.use(
  (config) => {
    console.log('Making request to:', config.url);
    return config;
  },
  (error) => {
    console.error('Request error:', error);
    return Promise.reject(error);
  }
);

// Add response interceptor for error handling
api.interceptors.response.use(
  (response) => {
    console.log('Received response from:', response.config.url);
    return response;
  },
  (error) => {
    console.error('Response error:', {
      url: error.config?.url,
      status: error.response?.status,
      message: error.message,
      data: error.response?.data
    });
    return Promise.reject(error);
  }
);

// Auth API
export const authAPI = {
  register: (userData) => api.post('/auth/register', userData),
  login: (credentials) => api.post('/auth/login', credentials),
  logout: () => api.post('/auth/logout'),
  getMe: () => api.get('/auth/me'),
  updateWallet: (walletAddress) => api.put('/auth/wallet', { walletAddress })
};

// Document API
export const documentAPI = {
  createDocument: (documentData) => api.post('/documents', documentData),
  getUserDocuments: () => api.get('/documents/user'),
  getDocument: (id) => api.get(`/documents/${id}`),
  updateDocument: (id, documentData) => api.put(`/documents/${id}`, documentData),
  updateDocumentBlockchain: (id, blockchainData) => api.put(`/documents/${id}/blockchain`, blockchainData),
  deleteDocument: (id) => api.delete(`/documents/${id}`)
};

// DAO API
export const daoAPI = {
  createDAO: (daoData) => api.post('/daos', daoData),
  getUserDAOs: () => api.get('/daos/user/me'),
  getDAO: (id) => api.get(`/daos/${id}`),
  updateDAO: (id, daoData) => api.put(`/daos/${id}`, daoData),
  updateDAOBlockchain: (id, blockchainData) => api.put(`/daos/${id}/blockchain`, blockchainData),
  deleteDAO: (id) => api.delete(`/daos/${id}`)
};

// AI API
export const aiAPI = {
  chat: async (message, conversation) => {
    try {
      console.log('Sending chat request:', { message, conversationLength: conversation.length });
      
      const response = await api.post('/ai/chat', {
        message,
        conversation: conversation.map(msg => ({
          role: msg.role,
          content: msg.content
        }))
      });
      
      console.log('Chat response received:', {
        status: response.status,
        hasData: !!response.data,
        dataKeys: response.data ? Object.keys(response.data) : []
      });
      
      return response.data;
    } catch (error) {
      console.error('Error in chat API call:', {
        message: error.message,
        response: error.response?.data
      });
      throw error;
    }
  },

  generateDocument: async (data) => {
    try {
      const response = await api.post('/ai/generate-document', data);
      return response.data;
    } catch (error) {
      console.error('Error generating document:', error);
      throw error;
    }
  },

  reviewContract: async (text) => {
    try {
      const response = await api.post('/ai/review-contract', { text });
      return response.data;
    } catch (error) {
      console.error('Error reviewing contract:', error);
      throw error;
    }
  },

  reviewContractImage: async (imageData) => {
    try {
      const response = await api.post('/ai/review-contract-image', { image: imageData });
      return response.data;
    } catch (error) {
      console.error('Error reviewing contract image:', error);
      throw error;
    }
  }
};

export default api;
