import React, { Suspense, useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import { AuthProvider } from './contexts/AuthContext';
import { Web3Provider } from './contexts/Web3Context';

// Pages
import Dashboard from './pages/Dashboard';
import DocumentGenerator from './pages/DocumentGenerator';
import ContractReviewer from './pages/ContractReviewer';
import DAOCreator from './pages/DAOCreator';
import LegalChatbot from './pages/LegalChatbot';
import Login from './pages/Login';
import Register from './pages/Register';
import Home from './pages/Home';

// Components
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import PrivateRoute from './components/PrivateRoute';
import Loading from './components/Loading';
import VideoPlayer from './components/VideoPlayer';

// Lazy load pages for better performance
const DashboardLazy = React.lazy(() => import('./pages/Dashboard'));
const DocumentGeneratorLazy = React.lazy(() => import('./pages/DocumentGenerator'));
const ContractReviewerLazy = React.lazy(() => import('./pages/ContractReviewer'));
const DAOCreatorLazy = React.lazy(() => import('./pages/DAOCreator'));
const LegalChatbotLazy = React.lazy(() => import('./pages/LegalChatbot'));
const HomeLazy = React.lazy(() => import('./pages/Home'));

function App() {
  const [isMobile, setIsMobile] = useState(false);

  // Check viewport width to choose between desktop and mobile video
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768); // You can adjust this breakpoint as needed
    };

    window.addEventListener('resize', handleResize);
    handleResize(); // Initial check on mount

    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Reference the correct video file from the public/assets folder
  const videoSrc = isMobile ? '/assets/Load2.mp4' : '/assets/Load.mp4';

  return (
    <AuthProvider>
      <Web3Provider>
        <div className="flex flex-col min-h-screen">
          <Navbar />
          {/* VideoPlayer can be placed on a landing/home page or as a background element */}
          <VideoPlayer src={videoSrc} className="w-full" />
          <main className="flex-grow">
            <Suspense fallback={<Loading showVideo={false} message="Loading page..." />}>
              <Routes>
                {/* Routes can be protected or public depending on your app requirements */}
                {/* <Route path="/login" element={<Login />} /> */}
                {/* <Route path="/register" element={<Register />} /> */}
                <Route path="/dashboard" element={<DashboardLazy />} />
                <Route path="/document-generator" element={<DocumentGeneratorLazy />} />
                <Route path="/contract-reviewer" element={<ContractReviewerLazy />} />
                <Route path="/dao-creator" element={<DAOCreatorLazy />} />
                <Route path="/legal-chatbot" element={<LegalChatbotLazy />} />
                <Route path="/" element={<HomeLazy />} />
              </Routes>
            </Suspense>
          </main>
          <Footer />
        </div>
      </Web3Provider>
    </AuthProvider>
  );
}

export default App;
