import React, { useState, useRef } from 'react';
import { aiAPI } from '../utils/api';

const ContractReviewer = () => {
  const [contractText, setContractText] = useState('');
  const [review, setReview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [uploadedImage, setUploadedImage] = useState(null);
  const [inputMethod, setInputMethod] = useState('text'); // 'text' or 'image'
  const fileInputRef = useRef(null);

  // Handle contract text change
  const handleTextChange = (e) => {
    setContractText(e.target.value);
  };

  // Convert image to base64
  const convertImageToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => {
        try {
          // Get the base64 string
          const base64String = reader.result;
          // Validate the base64 string
          if (!base64String || typeof base64String !== 'string') {
            reject(new Error('Invalid image data'));
            return;
          }
          resolve(base64String);
        } catch (error) {
          console.error('Error processing image:', error);
          reject(error);
        }
      };
      reader.onerror = (error) => {
        console.error('Error reading file:', error);
        reject(error);
      };
      reader.readAsDataURL(file);
    });
  };

  // Handle file upload
  const handleFileChange = async (e) => {
    try {
      const file = e.target.files[0];
      if (!file) return;

      // Check if file is an image
      if (!file.type.match('image.*')) {
        setError('Please upload an image file (JPEG, PNG, etc.)');
        return;
      }

      // Check file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setError('File size should be less than 5MB');
        return;
      }

      setUploadedImage(file);
      setError('');

      // Preview the image
      const base64String = await convertImageToBase64(file);
      const preview = document.getElementById('image-preview');
      if (preview) {
        preview.src = base64String;
      }
    } catch (error) {
      console.error('Error handling file:', error);
      setError('Error processing image. Please try again.');
    }
  };

  // Handle review submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (inputMethod === 'text' && !contractText.trim()) {
      setError('Please enter contract text to review');
      return;
    }

    if (inputMethod === 'image' && !uploadedImage) {
      setError('Please upload an image of the contract to review');
      return;
    }

    setLoading(true);
    setError('');
    setReview(null);

    try {
      let result;

      if (inputMethod === 'text') {
        console.log('Reviewing contract text, length:', contractText.length);
        result = await aiAPI.reviewContract(contractText);
      } else {
        console.log('Reviewing contract from image:', uploadedImage.name);
        // Convert image to base64
        const base64String = await convertImageToBase64(uploadedImage);
        if (!base64String) {
          throw new Error('Failed to process image');
        }
        result = await aiAPI.reviewContractImage(base64String);
      }

      console.log('Review result:', result);

      if (result.data.success) {
        setReview(result.data.data);
      } else {
        setError(result.data.message || 'Failed to review contract. Please try again.');
      }
    } catch (error) {
      console.error('Error reviewing contract:', error);
      setError(error.response?.data?.message || error.message || 'An unexpected error occurred. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Sample contract text
  const sampleContractText = `RENTAL AGREEMENT

THIS RENTAL AGREEMENT is made on this 10th day of June, 2023, between:

Mr. Rajesh Kumar, S/o Late Sh. Mohan Kumar, R/o 123, Green Park, New Delhi - 110016 (hereinafter referred to as the "LANDLORD")

AND

Ms. Priya Singh, D/o Sh. Ajay Singh, R/o 456, Vasant Kunj, New Delhi - 110070 (hereinafter referred to as the "TENANT")

WHEREAS the Landlord is the owner of residential premises situated at 789, Hauz Khas, New Delhi - 110016 (hereinafter referred to as the "PREMISES")

AND WHEREAS the Tenant has approached the Landlord to take the Premises on rent for residential purposes, and the Landlord has agreed to let out the same on the terms and conditions appearing hereinafter.

NOW THIS AGREEMENT WITNESSETH AS FOLLOWS:

1. RENT AND DEPOSIT:
   a) The monthly rent for the Premises shall be Rs. 25,000/- (Rupees Twenty-Five Thousand Only).
   b) The Tenant shall pay a security deposit of Rs. 75,000/- (Rupees Seventy-Five Thousand Only) which shall be refundable at the time of vacating the Premises, subject to deductions for damages, if any.

2. TERM:
   This Agreement shall be valid for a period of 11 (Eleven) months commencing from 15th June, 2023 to 14th May, 2024.

3. OBLIGATIONS OF THE TENANT:
   a) Pay the rent by the 5th day of each month.
   b) Pay for electricity, water, and maintenance charges as per actual consumption.
   c) Not sublet the Premises or any part thereof.
   d) Maintain the Premises in good condition.
   e) Not make any structural changes without written permission from the Landlord.

4. TERMINATION:
   Either party may terminate this Agreement by giving one month's notice in writing.

IN WITNESS WHEREOF, the parties have executed this Agreement on the date first above written.

________________________
(Landlord)

________________________
(Tenant)

WITNESSES:
1. ________________________
2. ________________________`;

  // Load sample text
  const loadSampleText = () => {
    setContractText(sampleContractText);
    setInputMethod('text');
    setUploadedImage(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Toggle input method
  const toggleInputMethod = (method) => {
    setInputMethod(method);
    setError('');

    if (method === 'text') {
      setUploadedImage(null);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-5xl mx-auto">
        <h1 className="text-2xl font-bold text-gray-900 dark:text-gray-100 mb-6">Legal Contract Reviewer</h1>

        <div className="card p-6 mb-6">
          <div className="mb-6">
            <h2 className="text-lg font-medium text-gray-900 dark:text-gray-100 mb-4">Upload or Enter Contract</h2>

            <div className="flex space-x-4 mb-4">
              <button
                type="button"
                onClick={() => toggleInputMethod('text')}
                className={`px-4 py-2 text-sm font-medium rounded-md shadow-sm transition-all duration-200 ${
                  inputMethod === 'text'
                    ? 'bg-indigo-600 dark:bg-indigo-700 text-white shadow-md'
                    : 'bg-gray-100 dark:bg-gray-700 text-gray-700 dark:text-gray-200 hover:bg-gray-200 dark:hover:bg-gray-600'
                }`}
              >
                Enter Text
              </button>
              <button
                type="button"
                onClick={() => toggleInputMethod('image')}
                className={`px-4 py-2 text-sm font-medium rounded-md shadow-sm transition-all duration-200 ${
                  inputMethod === 'image'
                    ? 'bg-indigo-600 dark:bg-indigo-700 text-white shadow-md'
                    : 'bg-gray-100 dark:bg-gray-700 text-gray-700 dark:text-gray-200 hover:bg-gray-200 dark:hover:bg-gray-600'
                }`}
              >
                Upload Image
              </button>
            </div>
          </div>

          <form onSubmit={handleSubmit}>
            {inputMethod === 'text' ? (
              <div className="mb-4">
                <label htmlFor="contractText" className="form-label">
                  Contract Text
                </label>
                <textarea
                  id="contractText"
                  name="contractText"
                  rows={15}
                  value={contractText}
                  onChange={handleTextChange}
                  placeholder="Paste your contract text here..."
                  className="input-field"
                ></textarea>
                <div className="mt-2 text-right">
                  <button
                    type="button"
                    onClick={loadSampleText}
                    className="text-sm text-indigo-600 dark:text-indigo-400 hover:text-indigo-500 dark:hover:text-indigo-300 transition-colors duration-200"
                  >
                    Load Sample Contract
                  </button>
                </div>
              </div>
            ) : (
              <div className="mb-4">
                <label htmlFor="contractImage" className="form-label">
                  Contract Image
                </label>
                <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 dark:border-gray-600 border-dashed rounded-md hover:border-indigo-500 dark:hover:border-indigo-400 transition-colors duration-200">
                  <div className="space-y-1 text-center">
                    {uploadedImage ? (
                      <div>
                        <img
                          id="image-preview"
                          src="#"
                          alt="Contract Preview"
                          className="mx-auto h-64 object-contain mb-4 rounded-md shadow-sm"
                        />
                        <p className="text-sm text-gray-500 dark:text-gray-400">{uploadedImage.name}</p>
                      </div>
                    ) : (
                      <div>
                        <svg
                          className="mx-auto h-12 w-12 text-gray-400 dark:text-gray-500"
                          stroke="currentColor"
                          fill="none"
                          viewBox="0 0 48 48"
                          aria-hidden="true"
                        >
                          <path
                            d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4l-3.172-3.172a4 4 0 00-5.656 0L28 28M8 32l9.172-9.172a4 4 0 015.656 0L28 28m0 0l4 4m4-24h8m-4-4v8m-12 4h.02"
                            strokeWidth={2}
                            strokeLinecap="round"
                            strokeLinejoin="round"
                          />
                        </svg>
                        <div className="flex text-sm text-gray-600 dark:text-gray-400 justify-center">
                          <label
                            htmlFor="file-upload"
                            className="relative cursor-pointer rounded-md font-medium text-indigo-600 dark:text-indigo-400 hover:text-indigo-500 dark:hover:text-indigo-300 focus-within:outline-none focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-indigo-500 dark:focus:within:ring-offset-gray-800"
                          >
                            <span>Upload a file</span>
                            <input
                              id="file-upload"
                              name="file-upload"
                              type="file"
                              className="sr-only"
                              onChange={handleFileChange}
                              accept="image/*"
                              ref={fileInputRef}
                            />
                          </label>
                          <p className="pl-1">or drag and drop</p>
                        </div>
                        <p className="text-xs text-gray-500 dark:text-gray-400">
                          PNG, JPG, GIF up to 5MB
                        </p>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            )}

            {error && (
              <div className="mb-4 p-3 bg-red-100 dark:bg-red-900/30 text-red-700 dark:text-red-300 rounded-md shadow-sm">
                {error}
              </div>
            )}

            <div className="flex justify-end">
              <button
                type="submit"
                disabled={loading}
                className="btn-primary flex items-center"
              >
                {loading ? (
                  <>
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Analyzing...
                  </>
                ) : (
                  'Review Contract'
                )}
              </button>
            </div>
          </form>
        </div>

        {review && (
          <div className="card p-6">
            <h2 className="text-xl font-bold text-gray-900 dark:text-gray-100 mb-4">Contract Review</h2>

            <div className="mb-6">
              <h3 className="text-lg font-medium text-gray-900 dark:text-gray-100 mb-2">Summary</h3>
              <p className="text-gray-700 dark:text-gray-300">{review.summary}</p>
            </div>

            <div className="mb-6">
              <h3 className="text-lg font-medium text-gray-900 dark:text-gray-100 mb-2">Potential Risks</h3>
              {review.risks && review.risks.length > 0 ? (
                <ul className="list-disc pl-5 space-y-1">
                  {review.risks.map((risk, index) => (
                    <li key={index} className="text-gray-700 dark:text-gray-300">{risk}</li>
                  ))}
                </ul>
              ) : (
                <p className="text-gray-500 dark:text-gray-400">No significant risks identified.</p>
              )}
            </div>

            <div className="mb-6">
              <h3 className="text-lg font-medium text-gray-900 dark:text-gray-100 mb-2">Clarifications Needed</h3>
              {review.clarifications && review.clarifications.length > 0 ? (
                <ul className="list-disc pl-5 space-y-1">
                  {review.clarifications.map((item, index) => (
                    <li key={index} className="text-gray-700 dark:text-gray-300">{item}</li>
                  ))}
                </ul>
              ) : (
                <p className="text-gray-500 dark:text-gray-400">No clarifications needed.</p>
              )}
            </div>

            <div className="mb-6">
              <h3 className="text-lg font-medium text-gray-900 dark:text-gray-100 mb-2">Best Practices</h3>
              {review.bestPractices && review.bestPractices.length > 0 ? (
                <ul className="list-disc pl-5 space-y-1">
                  {review.bestPractices.map((practice, index) => (
                    <li key={index} className="text-gray-700 dark:text-gray-300">{practice}</li>
                  ))}
                </ul>
              ) : (
                <p className="text-gray-500 dark:text-gray-400">No additional best practices suggested.</p>
              )}
            </div>

            <div>
              <h3 className="text-lg font-medium text-gray-900 dark:text-gray-100 mb-2">Final Advice</h3>
              <p className="text-gray-700 dark:text-gray-300">{review.finalAdvice}</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ContractReviewer;
