import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider } from './contexts/AuthContext';
import { Web3Provider } from './contexts/Web3Context';

// Pages
import Dashboard from './pages/Dashboard';
import DocumentGenerator from './pages/DocumentGenerator';
import ContractReviewer from './pages/ContractReviewer';
import DAOCreator from './pages/DAOCreator';
import LegalChatbot from './pages/LegalChatbot';
import Login from './pages/Login';
import Register from './pages/Register';
import Home from './pages/Home';

// Components
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import PrivateRoute from './components/PrivateRoute';
import LoadingScreen from './components/LoadingScreen';

function App() {
  const [showContent, setShowContent] = useState(false);

  // Simulate document load event
  useEffect(() => {
    window.addEventListener('load', () => {
      document.documentElement.style.overflow = 'hidden';
    });

    return () => {
      document.documentElement.style.overflow = 'auto';
    };
  }, []);

  const handleLoadingComplete = () => {
    setShowContent(true);
    document.documentElement.style.overflow = 'auto';
  };

  return (
    <AuthProvider>
      <Web3Provider>
        <LoadingScreen onLoadingComplete={handleLoadingComplete} />

        {showContent && (
          <div className="flex flex-col min-h-screen">
            <Navbar />
            <main className="flex-grow">
              <Routes>
                {/* Commenting out login and register routes for now */}
                {/* <Route path="/login" element={<Login />} /> */}
                {/* <Route path="/register" element={<Register />} /> */}

                {/* Making dashboard publicly accessible for now */}
                <Route path="/dashboard" element={<Dashboard />} />
                <Route path="/document-generator" element={<DocumentGenerator />} />
                <Route path="/contract-reviewer" element={<ContractReviewer />} />
                <Route path="/dao-creator" element={<DAOCreator />} />
                <Route path="/legal-chatbot" element={<LegalChatbot />} />
                <Route path="/" element={<Home />} />
              </Routes>
            </main>
            <Footer />
          </div>
        )}
      </Web3Provider>
    </AuthProvider>
  );
}

export default App;
