import React, { useState, useRef } from 'react';
import { aiAPI } from '../utils/api';

const ContractReviewer = () => {
  const [contractText, setContractText] = useState('');
  const [review, setReview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [uploadedImage, setUploadedImage] = useState(null);
  const [inputMethod, setInputMethod] = useState('text'); // 'text' or 'image'
  const [extractedText, setExtractedText] = useState('');
  const [showExtractedText, setShowExtractedText] = useState(false);
  const fileInputRef = useRef(null);

  // Handle contract text change
  const handleTextChange = (e) => {
    setContractText(e.target.value);
  };

  // Convert image to base64
  const convertImageToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => {
        try {
          // Get the base64 string
          const base64String = reader.result;
          // Validate the base64 string
          if (!base64String || typeof base64String !== 'string') {
            reject(new Error('Invalid image data'));
            return;
          }
          resolve(base64String);
        } catch (error) {
          console.error('Error processing image:', error);
          reject(error);
        }
      };
      reader.onerror = (error) => {
        console.error('Error reading file:', error);
        reject(error);
      };
      reader.readAsDataURL(file);
    });
  };

  // Handle file upload
  const handleFileChange = async (e) => {
    try {
      const file = e.target.files[0];
      if (!file) return;

      // Check if file is an image
      if (!file.type.match('image.*')) {
        setError('Please upload an image file (JPEG, PNG, etc.)');
        return;
      }

      // Check file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setError('File size should be less than 5MB');
        return;
      }

      setUploadedImage(file);
      setError('');

      // Preview the image
      const base64String = await convertImageToBase64(file);
      const preview = document.getElementById('image-preview');
      if (preview) {
        preview.src = base64String;
      }
    } catch (error) {
      console.error('Error handling file:', error);
      setError('Error processing image. Please try again.');
    }
  };

  // Handle review submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (inputMethod === 'text' && !contractText.trim()) {
      setError('Please enter contract text to review');
      return;
    }

    if (inputMethod === 'image' && !uploadedImage) {
      setError('Please upload an image of the contract to review');
      return;
    }

    setLoading(true);
    setError('');
    setReview(null);
    setExtractedText('');
    setShowExtractedText(false);

    try {
      let result;

      if (inputMethod === 'text') {
        console.log('Reviewing contract text, length:', contractText.length);
        result = await aiAPI.reviewContract(contractText);
      } else {
        console.log('Reviewing contract from image:', uploadedImage.name);
        // Show a more detailed loading message
        setError('Processing image and extracting text. This may take a moment...');

        // Convert image to base64
        const base64String = await convertImageToBase64(uploadedImage);
        if (!base64String) {
          throw new Error('Failed to process image');
        }

        console.log('Image converted to base64, length:', base64String.length);

        try {
          result = await aiAPI.reviewContractImage(base64String);
          console.log('Image review result:', result);
        } catch (imageError) {
          console.error('Error in image review:', imageError);
          // If there's a network error, create a fallback result
          if (imageError.message.includes('Network Error')) {
            result = {
              data: {
                success: true,
                data: {
                  summary: "This appears to be a standard contract document. (Fallback response due to network error)",
                  risks: [
                    "Unable to analyze risks due to connection issues",
                    "Consider uploading a clearer image or using text input instead"
                  ],
                  clarifications: ["Network error occurred during processing"],
                  bestPractices: ["Ensure stable internet connection", "Try using text input for more reliable results"],
                  finalAdvice: "Due to network issues, we couldn't properly analyze your document. Please try again or use the text input method instead.",
                  extractedText: "Could not extract text due to network error"
                }
              }
            };
          } else {
            throw imageError; // Re-throw if it's not a network error
          }
        }

        // If there's extracted text in the response, save it
        if (result.data.data.extractedText) {
          setExtractedText(result.data.data.extractedText);
          setShowExtractedText(true); // Automatically show extracted text for image uploads
        }
      }

      console.log('Review result:', result);

      if (result.data.success) {
        setReview(result.data.data);
        setError(''); // Clear any loading messages
      } else {
        setError(result.data.message || 'Failed to review contract. Please try again.');
      }
    } catch (error) {
      console.error('Error reviewing contract:', error);
      setError(error.response?.data?.message || error.message || 'An unexpected error occurred. Please try again.');

      // Create a fallback review for UI display even in case of error
      setReview({
        summary: "Error analyzing the contract",
        risks: ["Unable to complete analysis due to an error"],
        clarifications: ["Please try again or use a different input method"],
        bestPractices: ["Ensure the contract text or image is clear and readable"],
        finalAdvice: "We encountered an error while analyzing your contract. Please try again with a different format or contact support if the issue persists."
      });
    } finally {
      setLoading(false);
    }
  };

  // Sample contract text
  const sampleContractText = `RENTAL AGREEMENT

THIS RENTAL AGREEMENT is made on this 10th day of June, 2023, between:

Mr. Rajesh Kumar, S/o Late Sh. Mohan Kumar, R/o 123, Green Park, New Delhi - 110016 (hereinafter referred to as the "LANDLORD")

AND

Ms. Priya Singh, D/o Sh. Ajay Singh, R/o 456, Vasant Kunj, New Delhi - 110070 (hereinafter referred to as the "TENANT")

WHEREAS the Landlord is the owner of residential premises situated at 789, Hauz Khas, New Delhi - 110016 (hereinafter referred to as the "PREMISES")

AND WHEREAS the Tenant has approached the Landlord to take the Premises on rent for residential purposes, and the Landlord has agreed to let out the same on the terms and conditions appearing hereinafter.

NOW THIS AGREEMENT WITNESSETH AS FOLLOWS:

1. RENT AND DEPOSIT:
   a) The monthly rent for the Premises shall be Rs. 25,000/- (Rupees Twenty-Five Thousand Only).
   b) The Tenant shall pay a security deposit of Rs. 75,000/- (Rupees Seventy-Five Thousand Only) which shall be refundable at the time of vacating the Premises, subject to deductions for damages, if any.

2. TERM:
   This Agreement shall be valid for a period of 11 (Eleven) months commencing from 15th June, 2023 to 14th May, 2024.

3. OBLIGATIONS OF THE TENANT:
   a) Pay the rent by the 5th day of each month.
   b) Pay for electricity, water, and maintenance charges as per actual consumption.
   c) Not sublet the Premises or any part thereof.
   d) Maintain the Premises in good condition.
   e) Not make any structural changes without written permission from the Landlord.

4. TERMINATION:
   Either party may terminate this Agreement by giving one month's notice in writing.

IN WITNESS WHEREOF, the parties have executed this Agreement on the date first above written.

________________________
(Landlord)

________________________
(Tenant)

WITNESSES:
1. ________________________
2. ________________________`;

  // Load sample text
  const loadSampleText = () => {
    setContractText(sampleContractText);
    setInputMethod('text');
    setUploadedImage(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Toggle input method
  const toggleInputMethod = (method) => {
    setInputMethod(method);
    setError('');

    if (method === 'text') {
      setUploadedImage(null);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  // Render the review results
  const renderReview = () => {
    if (!review) return null;

    return (
      <div className="mt-6 bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold mb-4">Contract Review</h2>

        {inputMethod === 'image' && extractedText && (
          <div className="mb-4">
            <button
              onClick={() => setShowExtractedText(!showExtractedText)}
              className="mb-2 text-blue-600 hover:text-blue-800 font-medium"
            >
              {showExtractedText ? 'Hide Extracted Text' : 'Show Extracted Text'}
            </button>

            {showExtractedText && (
              <div className="p-4 bg-gray-50 rounded border border-gray-200 mb-4 max-h-96 overflow-y-auto">
                <h3 className="text-lg font-medium mb-2">Extracted Text from Image</h3>
                <pre className="whitespace-pre-wrap text-sm">{extractedText}</pre>
              </div>
            )}
          </div>
        )}

        <div className="mb-4">
          <h3 className="text-lg font-medium mb-2">Document Summary</h3>
          <p className="text-gray-700">{review.summary}</p>
        </div>

        {review.risks && review.risks.length > 0 && (
          <div className="mb-4">
            <h3 className="text-lg font-medium mb-2">Potential Risks</h3>
            <ul className="list-disc pl-5">
              {review.risks.map((risk, index) => (
                <li key={index} className="text-gray-700 mb-1">{risk}</li>
              ))}
            </ul>
          </div>
        )}

        {review.clarifications && review.clarifications.length > 0 && (
          <div className="mb-4">
            <h3 className="text-lg font-medium mb-2">Clarifications Needed</h3>
            <ul className="list-disc pl-5">
              {review.clarifications.map((item, index) => (
                <li key={index} className="text-gray-700 mb-1">{item}</li>
              ))}
            </ul>
          </div>
        )}

        {review.bestPractices && review.bestPractices.length > 0 && (
          <div className="mb-4">
            <h3 className="text-lg font-medium mb-2">Best Practices</h3>
            <ul className="list-disc pl-5">
              {review.bestPractices.map((item, index) => (
                <li key={index} className="text-gray-700 mb-1">{item}</li>
              ))}
            </ul>
          </div>
        )}

        <div className="mt-4 p-4 bg-blue-50 rounded border border-blue-200">
          <h3 className="text-lg font-medium mb-2">Final Advice</h3>
          <p className="text-gray-700">{review.finalAdvice}</p>
        </div>
      </div>
    );
  };

  return (
    <div className="bg-gray-50 min-h-screen py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="max-w-4xl mx-auto">
          <h1 className="text-2xl font-bold text-gray-900 mb-6">Legal Contract Reviewer</h1>

          <div className="bg-white shadow rounded-lg p-6 mb-6">
            <div className="mb-6">
              <h2 className="text-lg font-medium text-gray-900 mb-4">Upload or Enter Contract</h2>

              <div className="flex space-x-4 mb-4">
                <button
                  type="button"
                  onClick={() => toggleInputMethod('text')}
                  className={`px-4 py-2 text-sm font-medium rounded-md ${
                    inputMethod === 'text'
                      ? 'bg-indigo-600 text-white'
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  Enter Text
                </button>
                <button
                  type="button"
                  onClick={() => toggleInputMethod('image')}
                  className={`px-4 py-2 text-sm font-medium rounded-md ${
                    inputMethod === 'image'
                      ? 'bg-indigo-600 text-white'
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  Upload Image
                </button>
              </div>
            </div>

            <form onSubmit={handleSubmit}>
              {inputMethod === 'text' ? (
                <div className="mb-4">
                  <label htmlFor="contractText" className="block text-sm font-medium text-gray-700 mb-2">
                    Contract Text
                  </label>
                  <textarea
                    id="contractText"
                    name="contractText"
                    rows={15}
                    value={contractText}
                    onChange={handleTextChange}
                    placeholder="Paste your contract text here..."
                    className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                  ></textarea>
                  <div className="mt-2 text-right">
                    <button
                      type="button"
                      onClick={loadSampleText}
                      className="text-sm text-indigo-600 hover:text-indigo-500"
                    >
                      Load Sample Contract
                    </button>
                  </div>
                </div>
              ) : (
                <div className="mb-4">
                  <label htmlFor="contractImage" className="block text-sm font-medium text-gray-700 mb-2">
                    Contract Image
                  </label>
                  <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md">
                    <div className="space-y-1 text-center">
                      {uploadedImage ? (
                        <div>
                          <img
                            id="image-preview"
                            src="#"
                            alt="Contract Preview"
                            className="mx-auto h-64 object-contain mb-4"
                          />
                          <p className="text-sm text-gray-500">{uploadedImage.name}</p>
                        </div>
                      ) : (
                        <div>
                          <svg
                            className="mx-auto h-12 w-12 text-gray-400"
                            stroke="currentColor"
                            fill="none"
                            viewBox="0 0 48 48"
                            aria-hidden="true"
                          >
                            <path
                              d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4l-3.172-3.172a4 4 0 00-5.656 0L28 28M8 32l9.172-9.172a4 4 0 015.656 0L28 28m0 0l4 4m4-24h8m-4-4v8m-12 4h.02"
                              strokeWidth={2}
                              strokeLinecap="round"
                              strokeLinejoin="round"
                            />
                          </svg>
                          <div className="flex text-sm text-gray-600 justify-center">
                            <label
                              htmlFor="file-upload"
                              className="relative cursor-pointer bg-white rounded-md font-medium text-indigo-600 hover:text-indigo-500 focus-within:outline-none focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-indigo-500"
                            >
                              <span>Upload a file</span>
                              <input
                                id="file-upload"
                                name="file-upload"
                                type="file"
                                className="sr-only"
                                accept="image/*"
                                onChange={handleFileChange}
                                ref={fileInputRef}
                              />
                            </label>
                            <p className="pl-1">or drag and drop</p>
                          </div>
                          <p className="text-xs text-gray-500">PNG, JPG, GIF up to 5MB</p>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              )}

              {error && (
                <div className="mb-4 rounded-md bg-red-50 p-4">
                  <div className="flex">
                    <div className="ml-3">
                      <h3 className="text-sm font-medium text-red-800">{error}</h3>
                    </div>
                  </div>
                </div>
              )}

              <div className="flex justify-end">
                <button
                  type="submit"
                  disabled={loading || (inputMethod === 'text' && !contractText.trim()) || (inputMethod === 'image' && !uploadedImage)}
                  className="rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600 disabled:bg-indigo-400"
                >
                  {loading ? 'Analyzing...' : 'Review Contract'}
                </button>
              </div>
            </form>
          </div>

          {/* Review Results */}
          <div className="bg-white shadow rounded-lg p-6">
            <h2 className="text-lg font-medium text-gray-900 mb-4">Review Results</h2>

            {loading ? (
              <div className="flex justify-center items-center h-64">
                <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500"></div>
              </div>
            ) : review ? (
              renderReview()
            ) : (
              <div className="flex flex-col items-center justify-center h-64 text-center">
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-12 h-12 text-gray-400 mb-4">
                  <path strokeLinecap="round" strokeLinejoin="round" d="m11.25 11.25.041-.02a.75.75 0 0 1 1.063.852l-.708 2.836a.75.75 0 0 0 1.063.853l.041-.021M21 12a9 9 0 1 1-18 0 9 9 0 0 1 18 0Zm-9-3.75h.008v.008H12V8.25Z" />
                </svg>
                <p className="text-gray-500">Upload an image or enter your contract text and click "Review Contract" to get an analysis.</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ContractReviewer;
