import React, { useEffect, useRef, useState } from 'react';

const LoadingVideo = ({ mobileSrc, desktopSrc }) => {
  const videoRef = useRef(null);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [videoLoaded, setVideoLoaded] = useState(false);
  const [videoError, setVideoError] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const video = videoRef.current;
    if (video) {
      // Reset states when source changes
      setVideoLoaded(false);
      setVideoError(false);
      
      // Force load the video
      video.load();
      
      // Play the video when it's loaded
      const handleCanPlay = () => {
        setVideoLoaded(true);
        // Use a timeout to ensure DOM is ready
        setTimeout(() => {
          video.play().catch(error => {
            console.error('Error playing video:', error);
            setVideoError(true);
          });
        }, 100);
      };
      
      const handleError = (e) => {
        console.error('Video loading error:', e);
        setVideoError(true);
      };
      
      video.addEventListener('canplay', handleCanPlay);
      video.addEventListener('error', handleError);
      
      return () => {
        video.removeEventListener('canplay', handleCanPlay);
        video.removeEventListener('error', handleError);
      };
    }
  }, [isMobile, mobileSrc, desktopSrc]);

  return (
    <div className="relative w-64 h-64">
      {(!videoLoaded || videoError) && (
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500"></div>
        </div>
      )}
      {videoError && (
        <div className="absolute inset-0 flex items-center justify-center text-center text-sm text-red-500 mt-16">
          Video could not be loaded
        </div>
      )}
      <video 
        ref={videoRef}
        autoPlay 
        loop 
        muted 
        playsInline
        preload="auto"
        className={`w-full h-full ${videoLoaded && !videoError ? 'opacity-100' : 'opacity-0'} transition-opacity duration-300`}
      >
        <source src={isMobile ? `/assets/${mobileSrc}` : `/assets/${desktopSrc}`} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
    </div>
  );
};

export default LoadingVideo;
