import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { useAuth } from './context/AuthContext';

// Layout Components
import Navbar from './components/layout/Navbar';
import Footer from './components/layout/Footer';

// Pages
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import DocumentGenerator from './pages/DocumentGenerator';
import DocumentView from './pages/DocumentView';
import ContractReviewer from './pages/ContractReviewer';
import DAOCreator from './pages/DAOCreator';
import DAODashboard from './pages/DAODashboard';
import LegalChatbot from './pages/LegalChatbot';
import NotFound from './pages/NotFound';

// Protected Route Component
const ProtectedRoute = ({ children }) => {
  const { isAuthenticated, loading } = useAuth();
  
  if (loading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }
  
  if (!isAuthenticated) {
    return <Login />;
  }
  
  return children;
};

function App() {
  return (
    <div className="flex flex-col min-h-screen">
      <Navbar />
      <main className="flex-grow">
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          
          {/* Protected Routes */}
          <Route path="/dashboard" element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          } />
          
          <Route path="/document-generator" element={
            <ProtectedRoute>
              <DocumentGenerator />
            </ProtectedRoute>
          } />
          
          <Route path="/documents/:id" element={
            <ProtectedRoute>
              <DocumentView />
            </ProtectedRoute>
          } />
          
          <Route path="/contract-reviewer" element={
            <ProtectedRoute>
              <ContractReviewer />
            </ProtectedRoute>
          } />
          
          <Route path="/dao-creator" element={
            <ProtectedRoute>
              <DAOCreator />
            </ProtectedRoute>
          } />
          
          <Route path="/daos/:id" element={
            <ProtectedRoute>
              <DAODashboard />
            </ProtectedRoute>
          } />
          
          <Route path="/legal-chatbot" element={
            <ProtectedRoute>
              <LegalChatbot />
            </ProtectedRoute>
          } />
          
          {/* 404 Route */}
          <Route path="*" element={<NotFound />} />
        </Routes>
      </main>
      <Footer />
    </div>
  );
}

export default App;
