import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useWeb3 } from '../contexts/Web3Context';
import { documentAPI, daoAPI } from '../utils/api';
import PageBackground from '../components/PageBackground';

const Dashboard = () => {
  const { user } = useAuth();
  const { connected, account, connectWallet } = useWeb3();
  
  const [documents, setDocuments] = useState([]);
  const [daos, setDaos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  
  // Fetch user documents and DAOs
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        // Fetch documents
        const documentsResult = await documentAPI.getUserDocuments();
        if (documentsResult.success) {
          setDocuments(documentsResult.data);
        }
        
        // Fetch DAOs
        const daosResult = await daoAPI.getUserDAOs();
        if (daosResult.success) {
          setDaos(daosResult.data);
        }
      } catch (err) {
        console.error('Error fetching data:', err);
      } finally {
        setLoading(false);
      }
    };
    
    fetchData();
  }, []);
  
  const handleConnectWallet = async () => {
    try {
      await connectWallet();
    } catch (err) {
      console.error('Error connecting wallet:', err);
    }
  };

  return (
    <PageBackground>
      <div className="container mx-auto px-4 py-8">
        <div className="bg-white/80 dark:bg-gray-800/80 backdrop-blur-sm rounded-lg shadow-xl p-6 mb-8">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-6">Dashboard</h1>
          
          {/* Wallet Connection */}
          <div className="mb-8 p-6 bg-indigo-50/90 dark:bg-indigo-900/50 rounded-lg shadow-md">
            <h2 className="text-xl font-semibold text-gray-800 dark:text-white mb-4">Wallet Connection</h2>
            {connected && account ? (
              <div>
                <p className="text-gray-700 dark:text-gray-300 mb-2">Connected Account:</p>
                <div className="bg-green-100 dark:bg-green-800 text-green-800 dark:text-green-100 px-4 py-2 rounded-md font-mono">
                  {account}
                </div>
              </div>
            ) : (
              <div>
                <p className="text-gray-700 dark:text-gray-300 mb-4">Connect your wallet to access Web3 features.</p>
                <button
                  onClick={handleConnectWallet}
                  className="bg-indigo-600 hover:bg-indigo-700 text-white font-medium py-2 px-4 rounded-md transition duration-300"
                >
                  Connect Wallet
                </button>
              </div>
            )}
          </div>
          
          {/* Recent Documents */}
          <div className="mb-8">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-semibold text-gray-800 dark:text-white">Recent Documents</h2>
              <Link to="/document-generator" className="text-indigo-600 hover:text-indigo-800 dark:text-indigo-400 dark:hover:text-indigo-300">
                Create New Document
              </Link>
            </div>
            
            {loading ? (
              <div className="flex justify-center py-8">
                <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-indigo-500"></div>
              </div>
            ) : documents.length > 0 ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {documents.map((document) => (
                  <div key={document._id} className="bg-white dark:bg-gray-700 rounded-lg shadow-md p-4 hover:shadow-lg transition duration-300">
                    <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">{document.title}</h3>
                    <p className="text-sm text-gray-500 dark:text-gray-400 mb-4">
                      {new Date(document.createdAt).toLocaleDateString()}
                    </p>
                    <Link
                      to={`/document/${document._id}`}
                      className="text-indigo-600 hover:text-indigo-800 dark:text-indigo-400 dark:hover:text-indigo-300 text-sm font-medium"
                    >
                      View Document
                    </Link>
                  </div>
                ))}
              </div>
            ) : (
              <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-6 text-center">
                <p className="text-gray-600 dark:text-gray-300 mb-4">You haven't created any documents yet.</p>
                <Link
                  to="/document-generator"
                  className="inline-block bg-indigo-600 hover:bg-indigo-700 text-white font-medium py-2 px-4 rounded-md transition duration-300"
                >
                  Generate Your First Document
                </Link>
              </div>
            )}
          </div>
          
          {/* Your DAOs */}
          <div>
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-semibold text-gray-800 dark:text-white">Your DAOs</h2>
              <Link to="/dao-creator" className="text-indigo-600 hover:text-indigo-800 dark:text-indigo-400 dark:hover:text-indigo-300">
                Create New DAO
              </Link>
            </div>
            
            {loading ? (
              <div className="flex justify-center py-8">
                <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-indigo-500"></div>
              </div>
            ) : daos.length > 0 ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {daos.map((dao) => (
                  <div key={dao._id} className="bg-white dark:bg-gray-700 rounded-lg shadow-md p-4 hover:shadow-lg transition duration-300">
                    <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">{dao.name}</h3>
                    <p className="text-sm text-gray-500 dark:text-gray-400 mb-4">
                      {dao.members.length} members
                    </p>
                    <Link
                      to={`/dao/${dao._id}`}
                      className="text-indigo-600 hover:text-indigo-800 dark:text-indigo-400 dark:hover:text-indigo-300 text-sm font-medium"
                    >
                      View DAO
                    </Link>
                  </div>
                ))}
              </div>
            ) : (
              <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-6 text-center">
                <p className="text-gray-600 dark:text-gray-300 mb-4">You haven't created or joined any DAOs yet.</p>
                <Link
                  to="/dao-creator"
                  className="inline-block bg-indigo-600 hover:bg-indigo-700 text-white font-medium py-2 px-4 rounded-md transition duration-300"
                >
                  Create Your First DAO
                </Link>
              </div>
            )}
          </div>
        </div>
      </div>
    </PageBackground>
  );
};

export default Dashboard;
