import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import LoadingVideo from '../components/LoadingVideo';
import BackgroundImage from '../components/BackgroundImage';

const features = [
  {
    name: 'AI-Powered Document Generation',
    description: 'Generate legal documents like NDAs, employment contracts, and lease agreements in seconds using advanced AI.',
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
        <path strokeLinecap="round" strokeLinejoin="round" d="M19.5 14.25v-2.625a3.375 3.375 0 0 0-3.375-3.375h-1.5A1.125 1.125 0 0 1 13.5 7.125v-1.5a3.375 3.375 0 0 0-3.375-3.375H8.25m2.25 0H5.625c-.621 0-1.125.504-1.125 1.125v17.25c0 .621.504 1.125 1.125 1.125h12.75c.621 0 1.125-.504 1.125-1.125V11.25a9 9 0 0 0-9-9Z" />
      </svg>
    ),
    link: '/document-generator'
  },
  {
    name: 'Smart Contract Review',
    description: 'Analyze contracts for legal risks, best practices, and get expert recommendations.',
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
        <path strokeLinecap="round" strokeLinejoin="round" d="m11.25 11.25.041-.02a.75.75 0 0 1 1.063.852l-.708 2.836a.75.75 0 0 0 1.063.853l.041-.021M21 12a9 9 0 1 1-18 0 9 9 0 0 1 18 0Zm-9-3.75h.008v.008H12V8.25Z" />
      </svg>
    ),
    link: '/contract-reviewer'
  },
  {
    name: 'DAO Creation',
    description: 'Create and manage decentralized autonomous organizations (DAOs) with custom governance rules.',
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
        <path strokeLinecap="round" strokeLinejoin="round" d="M18 18.72a9.094 9.094 0 0 0 3.741-.479 3 3 0 0 0-4.682-2.72m.94 3.198.001.031c0 .225-.012.447-.037.666A11.944 11.944 0 0 1 12 21c-2.17 0-4.207-.576-5.963-1.584A6.062 6.062 0 0 1 6 18.719m12 0a5.971 5.971 0 0 0-.941-3.197m0 0A5.995 5.995 0 0 0 12 12.75a5.995 5.995 0 0 0-5.058 2.772m0 0a3 3 0 0 0-4.681 2.72 8.986 8.986 0 0 0 3.74.477m.94-3.197a5.971 5.971 0 0 0-.94 3.197M15 6.75a3 3 0 1 1-6 0 3 3 0 0 1 6 0Zm6 3a2.25 2.25 0 1 1-4.5 0 2.25 2.25 0 0 1 4.5 0Zm-13.5 0a2.25 2.25 0 1 1-4.5 0 2.25 2.25 0 0 1 4.5 0Z" />
      </svg>
    ),
    link: '/dao-creator'
  },
  {
    name: 'Legal Chatbot',
    description: 'Get instant legal advice and information through our conversational AI chatbot.',
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
        <path strokeLinecap="round" strokeLinejoin="round" d="M20.25 8.511c.884.284 1.5 1.128 1.5 2.097v4.286c0 1.136-.847 2.1-1.98 2.193-.34.027-.68.052-1.02.072v3.091l-3-3c-1.354 0-2.694-.055-4.02-.163a2.115 2.115 0 0 1-.825-.242m9.345-8.334a2.126 2.126 0 0 0-.476-.095 48.64 48.64 0 0 0-8.048 0c-1.131.094-1.976 1.057-1.976 2.192v4.286c0 .837.46 1.58 1.155 1.951m9.345-8.334V6.637c0-1.621-1.152-3.026-2.76-3.235A48.455 48.455 0 0 0 11.25 3c-2.115 0-4.198.137-6.24.402-1.608.209-2.76 1.614-2.76 3.235v6.226c0 1.621 1.152 3.026 2.76 3.235.577.075 1.157.14 1.74.194V21l4.155-4.155" />
      </svg>
    ),
    link: '/legal-chatbot'
  },
];

// Legal quotes for the dashboard
const legalQuotes = [
  "Justice is the constant and perpetual will to allot to every man his due. - Justinian I",
  "The law is reason, free from passion. - Aristotle",
  "It is not wisdom but authority that makes a law. - Thomas Hobbes",
  "Laws are silent in times of war. - Cicero",
  "The safety of the people shall be the highest law. - Cicero",
  "Justice delayed is justice denied. - William E. Gladstone",
  "The power of the lawyer is in the uncertainty of the law. - Jeremy Bentham",
  "Law is order, and good law is good order. - Aristotle",
  "In law, nothing is certain but the expense. - Samuel Butler",
  "Where there is no law, there is no freedom. - John Locke"
];

const LandingPage = () => {
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div className="bg-white">
      {/* Hero Section */}
      <div className="relative isolate overflow-hidden bg-gradient-to-b from-indigo-100/20">
        <div className="mx-auto max-w-7xl pb-24 pt-10 sm:pb-32 lg:grid lg:grid-cols-2 lg:gap-x-8 lg:px-8 lg:py-40">
          <div className="px-6 lg:px-0 lg:pt-4">
            <div className="mx-auto max-w-2xl">
              <div className="max-w-lg">
                <h1 className="mt-10 text-4xl font-bold tracking-tight text-gray-900 sm:text-6xl">
                  DigiLex
                </h1>
                <p className="mt-6 text-lg leading-8 text-gray-600">
                  Your AI-powered legal assistant for document generation, contract review, and expert legal guidance.
                </p>
                <div className="mt-10 flex items-center gap-x-6">
                  <a
                    href="/dashboard"
                    className="rounded-md bg-indigo-600 px-3.5 py-2.5 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
                  >
                    Get started
                  </a>
                  <a href="#about" className="text-sm font-semibold leading-6 text-gray-900">
                    Learn more <span aria-hidden="true">→</span>
                  </a>
                </div>
              </div>
            </div>
          </div>
          <div className="mt-20 sm:mt-24 md:mx-auto md:max-w-2xl lg:mx-0 lg:mt-0 lg:w-screen">
            <div className="absolute inset-y-0 right-1/2 -z-10 -mr-10 w-[200%] skew-x-[-30deg] bg-white shadow-xl shadow-indigo-600/10 ring-1 ring-indigo-50 md:-mr-20 lg:-mr-36" aria-hidden="true" />
            <div className="shadow-lg md:rounded-3xl">
              <div className="bg-indigo-500 [clip-path:inset(0)] md:[clip-path:inset(0_round_theme(borderRadius.3xl))]">
                <div className="absolute -inset-y-px left-1/2 -z-10 ml-10 w-[200%] skew-x-[-30deg] bg-indigo-100 opacity-20 ring-1 ring-inset ring-white md:ml-20 lg:ml-36" aria-hidden="true" />
                <div className="relative px-6 pt-8 sm:pt-16 md:pl-16 md:pr-0">
                  <div className="mx-auto max-w-2xl md:mx-0 md:max-w-none">
                    <div className="w-screen overflow-hidden rounded-tl-xl bg-gray-900">
                      <div className="flex bg-gray-800/40 ring-1 ring-white/5">
                        <div className="-mb-px flex text-sm font-medium leading-6 text-gray-400">
                          <div className="border-b border-r border-b-white/20 border-r-white/10 bg-white/5 px-4 py-2 text-white">
                            LegalAssistant.jsx
                          </div>
                          <div className="border-r border-gray-600/10 px-4 py-2">
                            App.jsx
                          </div>
                        </div>
                      </div>
                      <div className="px-6 pt-6 pb-14 bg-gray-900">
                        <pre className="text-sm text-gray-300">
                          <code>
                            <span className="text-indigo-400">import</span> React <span className="text-indigo-400">from</span> <span className="text-green-300">'react'</span>;{'\n'}
                            <span className="text-indigo-400">import</span> DigiLex <span className="text-indigo-400">from</span> <span className="text-green-300">'@digilex/core'</span>;{'\n\n'}
                            <span className="text-indigo-400">function</span> <span className="text-yellow-300">LegalAssistant</span>() {'{'}
                            {'\n  '}<span className="text-indigo-400">return</span> (
                            {'\n    '}<span className="text-gray-500">&lt;</span><span className="text-indigo-300">DigiLex.Provider</span><span className="text-gray-500">&gt;</span>
                            {'\n      '}<span className="text-gray-500">&lt;</span><span className="text-indigo-300">DigiLex.DocumentGenerator</span> <span className="text-indigo-300">type</span><span className="text-gray-500">=</span><span className="text-green-300">"contract"</span><span className="text-gray-500">/&gt;</span>
                            {'\n      '}<span className="text-gray-500">&lt;</span><span className="text-indigo-300">DigiLex.ContractReviewer</span><span className="text-gray-500">/&gt;</span>
                            {'\n      '}<span className="text-gray-500">&lt;</span><span className="text-indigo-300">DigiLex.LegalChatbot</span><span className="text-gray-500">/&gt;</span>
                            {'\n    '}<span className="text-gray-500">&lt;/</span><span className="text-indigo-300">DigiLex.Provider</span><span className="text-gray-500">&gt;</span>
                            {'\n  '});
                            {'\n}'}
                          </code>
                        </pre>
                      </div>
                    </div>
                  </div>
                  <div className="pointer-events-none absolute inset-0 ring-1 ring-inset ring-black/10 md:rounded-3xl" aria-hidden="true" />
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="absolute inset-x-0 bottom-0 -z-10 h-24 bg-gradient-to-t from-white sm:h-32" />
      </div>

      {/* About Us section */}
      <div id="about" className="relative overflow-hidden py-24 sm:py-32">
        <BackgroundImage 
          mobileSrc="bg2.png" 
          desktopSrc="bg.png" 
          opacity={0.9} 
          position="center" 
          size="cover" 
