import React, { useState, useRef, useEffect } from 'react';
import { aiAPI } from '../utils/api';

const LegalChatbot = () => {
  const [messages, setMessages] = useState([
    {
      role: 'assistant',
      content: 'नमस्ते! I\'m your DigiLex AI-powered legal assistant specialized in Indian law. I can provide information about Indian legal matters including constitutional law, criminal law, civil law, family law, corporate law, and more. How can I help you today?'
    }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const messagesEndRef = useRef(null);
  
  // Auto-scroll to bottom of messages
  useEffect(() => {
    scrollToBottom();
  }, [messages]);
  
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };
  
  const handleInputChange = (e) => {
    setInput(e.target.value);
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!input.trim()) return;
    
    // Reset error state
    setError(null);
    
    // Add user message to chat
    const userMessage = { role: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setLoading(true);
    
    try {
      // Get last 10 messages for context (or fewer if there aren't 10)
      const recentMessages = [...messages.slice(-9), userMessage];
      
      console.log('Sending request to:', process.env.REACT_APP_API_URL);
      console.log('Request payload:', { message: userMessage.content, conversation: recentMessages });
      
      // Send to API with a timeout of 30 seconds
      const response = await Promise.race([
        aiAPI.chat(userMessage.content, recentMessages),
        new Promise((_, reject) => 
          setTimeout(() => reject(new Error('Request timed out after 30 seconds')), 30000)
        )
      ]);
      
      console.log('API Response:', response); // Debug log
      
      if (!response || !response.data) {
        throw new Error('No valid response received from server');
      }

      // Extract the response text from the API response
      let responseText = '';
      
      if (response.data.success === true && response.data.data && response.data.data.response) {
        responseText = response.data.data.response;
      } else if (response.data.response) {
        responseText = response.data.response;
      } else {
        console.error('Unexpected response format:', response.data);
        throw new Error('Received an unexpected response format from the server');
      }
      
      console.log('Extracted response text:', responseText);
      
      // Add assistant response to chat
      setMessages(prev => [
        ...prev, 
        { role: 'assistant', content: responseText }
      ]);
    } catch (error) {
      console.error('Error sending message:', error);
      console.error('Error details:', {
        message: error.message,
        response: error.response?.data || 'No response data',
        status: error.response?.status || 'No status code',
        stack: error.stack
      });
      
      // Set error message for UI feedback
      const errorMessage = error.message || 'An error occurred while processing your request';
      setError(errorMessage);
      
      // Add error message to chat
      setMessages(prev => [
        ...prev, 
        { 
          role: 'assistant', 
          content: `Sorry, I encountered an error: ${errorMessage}. Please try again later.` 
        }
      ]);
    } finally {
      setLoading(false);
    }
  };
  
  const renderMessage = (message, index) => {
    const isUser = message.role === 'user';
    
    return (
      <div 
        key={index} 
        className={`mb-4 ${isUser ? 'text-right' : ''}`}
      >
        <div 
          className={`inline-block rounded-lg px-4 py-2 max-w-[80%] ${
            isUser 
              ? 'bg-indigo-600 text-white dark:bg-indigo-800' 
              : 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200'
          }`}
        >
          <p className="whitespace-pre-wrap">{message.content}</p>
        </div>
      </div>
    );
  };
  
  const renderSuggestions = () => {
    const suggestions = [
      "What are the key provisions of the Information Technology Act in India?",
      "Explain the process for starting a private limited company in India",
      "What are my rights as a tenant under Indian rental laws?",
      "How does the GST work for e-commerce businesses in India?",
      "What is the legal procedure for filing a consumer complaint in India?"
    ];
    
    return (
      <div className="mt-6">
        <h3 className="text-sm font-medium text-gray-500 dark:text-gray-400 mb-2">Suggested questions:</h3>
        <div className="flex flex-wrap gap-2">
          {suggestions.map((suggestion, index) => (
            <button
              key={index}
              onClick={() => {
                setInput(suggestion);
              }}
              className="text-xs bg-gray-100 hover:bg-gray-200 text-gray-800 dark:bg-gray-700 dark:text-gray-200 dark:hover:bg-gray-600 px-3 py-1 rounded-full transition-colors"
            >
              {suggestion}
            </button>
          ))}
        </div>
      </div>
    );
  };
  
  return (
    <div className="max-w-4xl mx-auto py-8 px-4 sm:px-6 lg:px-8">
      <div className="mb-6">
        <h1 className="text-2xl font-bold text-gray-900 dark:text-white">DigiLex Legal Assistant</h1>
        <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
          Get expert legal guidance on Indian laws, regulations, and procedures.
        </p>
        {error && (
          <div className="mt-2 p-2 bg-red-100 border border-red-400 text-red-700 rounded">
            Error: {error}
          </div>
        )}
      </div>
      
      <div className="bg-white dark:bg-gray-800 shadow rounded-lg overflow-hidden flex flex-col h-[70vh]">
        {/* Chat messages */}
        <div className="flex-1 overflow-y-auto p-4">
          {messages.map(renderMessage)}
          {loading && (
            <div className="flex items-center space-x-2 text-gray-500 dark:text-gray-400 mb-4">
              <div className="w-2 h-2 rounded-full bg-gray-400 dark:bg-gray-500 animate-bounce"></div>
              <div className="w-2 h-2 rounded-full bg-gray-400 dark:bg-gray-500 animate-bounce" style={{ animationDelay: '0.2s' }}></div>
              <div className="w-2 h-2 rounded-full bg-gray-400 dark:bg-gray-500 animate-bounce" style={{ animationDelay: '0.4s' }}></div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>
        
        {/* Message input */}
        <div className="border-t border-gray-200 dark:border-gray-700 p-4">
          <form onSubmit={handleSubmit} className="flex space-x-2">
            <input
              type="text"
              value={input}
              onChange={handleInputChange}
              placeholder="Type your legal question about Indian law here..."
              className="flex-1 rounded-md border-gray-300 dark:border-gray-600 dark:bg-gray-700 dark:text-white shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
              disabled={loading}
            />
            <button
              type="submit"
              disabled={loading || !input.trim()}
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 dark:bg-indigo-700 dark:hover:bg-indigo-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:bg-indigo-300 disabled:cursor-not-allowed"
            >
              {loading ? (
                <svg className="animate-spin h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
              ) : (
                <span>Send</span>
              )}
            </button>
          </form>
          
          {messages.length === 1 && renderSuggestions()}
        </div>
      </div>
      
      <div className="mt-6 bg-indigo-50 dark:bg-indigo-900 rounded-lg p-4">
        <h3 className="text-sm font-medium text-indigo-800 dark:text-indigo-200 mb-2">Disclaimer</h3>
        <p className="text-xs text-indigo-700 dark:text-indigo-300">
          This AI assistant provides general legal information and guidance related to Indian law. 
          The information provided should not be considered as legal advice. For specific legal matters, 
          please consult with a qualified attorney who specializes in the relevant area of Indian law.
          This service is not a substitute for professional legal counsel and does not create an attorney-client relationship.
        </p>
      </div>
    </div>
  );
};

export default LegalChatbot;
