import React, { useState, useRef, useEffect } from 'react';
import { aiAPI } from '../utils/api';

const LegalChatbot = () => {
  const [messages, setMessages] = useState([
    {
      role: 'assistant',
      content: 'नमस्ते! I\'m your DigiLex AI-powered legal assistant specialized in Indian law. I can provide information about Indian legal matters including constitutional law, criminal law, civil law, family law, corporate law, and more. How can I help you today?'
    }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const messagesEndRef = useRef(null);
  
  // Auto-scroll to bottom of messages
  useEffect(() => {
    scrollToBottom();
  }, [messages]);
  
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };
  
  const handleInputChange = (e) => {
    setInput(e.target.value);
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!input.trim()) return;
    
    // Reset error state
    setError(null);
    
    // Add user message to chat
    const userMessage = { role: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setLoading(true);
    
    try {
      // Get last 10 messages for context (or fewer if there aren't 10)
      const recentMessages = [...messages.slice(-9), userMessage];
      
      console.log('Sending request to:', process.env.REACT_APP_API_URL || 'http://localhost:5000/api');
      console.log('Request payload:', { message: userMessage.content, conversation: recentMessages });
      
      // Send to API - no need for Promise.race as we have a timeout in the API config
      const response = await aiAPI.chat(userMessage.content, recentMessages);
      
      console.log('API Response:', response); // Debug log
      
      if (!response || !response.data) {
        throw new Error('No valid response received from server');
      }

      // Extract the response text from the API response
      let responseText = '';
      
      if (response.data.success === true && response.data.data && response.data.data.response) {
        responseText = response.data.data.response;
        console.log('Successfully extracted response from data.data.response');
      } else if (response.data.response) {
        responseText = response.data.response;
        console.log('Successfully extracted response from data.response');
      } else if (typeof response.data === 'string') {
        // Handle case where response might be a direct string
        responseText = response.data;
        console.log('Response was a direct string');
      } else {
        console.error('Unexpected response format:', JSON.stringify(response.data));
        throw new Error('Received an unexpected response format from the server');
      }
      
      console.log('Extracted response text:', responseText);
      
      // Add assistant response to chat
      setMessages(prev => [
        ...prev, 
        { role: 'assistant', content: responseText }
      ]);
    } catch (error) {
      console.error('Error sending message:', error);
      console.error('Error details:', {
        message: error.message,
        response: error.response?.data || 'No response data',
        status: error.response?.status || 'No status code',
        stack: error.stack
      });
      
      // Set error message for UI feedback
      let errorMessage = 'Sorry, I encountered an error processing your request. Please try again later.';
      
      // Add more specific error messages based on error type
      if (error.message.includes('timeout')) {
        errorMessage = 'The request took too long to process. Please try a shorter or simpler question.';
      } else if (error.message.includes('Network Error') || !error.response) {
        errorMessage = 'Unable to connect to the server. Please check your internet connection and try again.';
      }
      
      setError(errorMessage);
      
      // Add error message to chat
      setMessages(prev => [
        ...prev, 
        { 
          role: 'assistant', 
          content: errorMessage
        }
      ]);
    } finally {
      setLoading(false);
    }
  };
  
  const renderMessage = (message, index) => {
    const isUser = message.role === 'user';
    
    return (
      <div 
        key={index} 
        className={`mb-4 ${isUser ? 'text-right' : ''}`}
      >
        <div 
          className={`inline-block rounded-lg px-4 py-2 max-w-[80%] ${
            isUser 
              ? 'bg-indigo-600 text-white dark:bg-indigo-800' 
              : 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200'
          }`}
        >
          <p className="whitespace-pre-wrap">{message.content}</p>
        </div>
      </div>
    );
  };
  
  const renderSuggestions = () => {
    const suggestions = [
      "What are the key provisions of the Information Technology Act in India?",
      "Explain the process for starting a private limited company in India",
      "What are my rights as a tenant under Indian rental laws?",
      "How does the GST work for e-commerce businesses in India?",
      "What is the legal procedure for filing a consumer complaint in India?"
    ];
    
    return (
      <div className="mt-6">
        <h3 className="text-sm font-medium text-gray-500 dark:text-gray-400 mb-2">Suggested questions:</h3>
        <div className="flex flex-wrap gap-2">
          {suggestions.map((suggestion, index) => (
            <button
              key={index}
              onClick={() => {
                setInput(suggestion);
              }}
              className="text-xs bg-gray-100 hover:bg-gray-200 text-gray-800 dark:bg-gray-700 dark:text-gray-200 dark:hover:bg-gray-600 px-3 py-1 rounded-full transition-colors"
            >
              {suggestion}
            </button>
          ))}
        </div>
      </div>
    );
  };
  
  return (
    <div className="container mx-auto px-4 py-8 max-w-4xl">
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg overflow-hidden">
        <div className="p-4 bg-indigo-600 dark:bg-indigo-800 text-white">
          <h1 className="text-xl font-bold">DigiLex Legal Assistant</h1>
          <p className="text-sm opacity-80">AI-powered legal assistant specialized in Indian law</p>
        </div>
        
        {/* Display error message at the top if there is one */}
        {error && (
          <div className="p-3 bg-red-100 border-l-4 border-red-500 text-red-700 mb-4">
            <p className="font-medium">Error</p>
            <p>{error}</p>
          </div>
        )}
        
        <div className="p-4 h-[60vh] overflow-y-auto bg-gray-50 dark:bg-gray-900">
          {messages.map(renderMessage)}
          <div ref={messagesEndRef} />
        </div>
        
        <div className="p-4 border-t border-gray-200 dark:border-gray-700">
          <form onSubmit={handleSubmit} className="flex items-center">
            <input
              type="text"
              value={input}
              onChange={handleInputChange}
              placeholder="Type your legal question here..."
              className="flex-grow p-2 border rounded-l-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 dark:bg-gray-700 dark:border-gray-600 dark:text-white"
              disabled={loading}
            />
            <button
              type="submit"
              className={`p-2 rounded-r-lg ${
                loading
                  ? 'bg-gray-400 cursor-not-allowed'
                  : 'bg-indigo-600 hover:bg-indigo-700'
              } text-white`}
              disabled={loading}
            >
              {loading ? (
                <svg className="animate-spin h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
              ) : (
                <span>Send</span>
              )}
            </button>
          </form>
          
          {messages.length <= 1 && renderSuggestions()}
        </div>
        
        <div className="p-4 bg-gray-100 dark:bg-gray-900 text-xs text-gray-500 dark:text-gray-400">
          <p className="font-medium">Disclaimer:</p>
          <p>This AI assistant provides general legal information based on Indian law. It is not a substitute for professional legal advice. Always consult with a qualified legal professional for specific legal matters.</p>
        </div>
      </div>
    </div>
  );
};

export default LegalChatbot;
